import React from "react";
import image from "./img/1.jpg";
import "./css/style.css";

export default function Avatar({ size = "m", type = "sqaure" }) {
  return (
    <div className="card">
      <div className="img">
        <img src={image} alt="pic">
          <p className={size}>{size}</p>
          <p className={size}>{type}</p>
        </img>
      </div>
    </div>
  );
}
